mapper.select:
select 

from tms_tray_info t
left join tms_rfid_bind_spurtcode s on s.rfid = t.rfid and s.factory_code =t.factory_code and s.is_having =0 and s.delete_flag =0
left join tms_rfid_bind_order_detail d on d.rfid = t.rfid and d.current_code = s.current_code and d.delete_flag =0
left join tms_rfid_bind_order o on o.id =d.pid and o.delete_flag=0
where t.delete_flag=0

  and t.rfid = #{rfid,jdbcType=VARCHAR}


  and t.rfid_type = #{rfidType,jdbcType=INTEGER}


  and t.rfid_status = #{rfidStatus,jdbcType=INTEGER}


  and t.rfid_health = #{rfidHealth,jdbcType=INTEGER}


     and date_format(t.init_time,'%Y-%m-%d') >= #{startTime,jdbcType=VARCHAR}


     and date_format(t.init_time,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}


    and o.order_no = #{orderNo,jdbcType=VARCHAR}


    and s.current_code = #{currentCode,jdbcType=VARCHAR}

order by t.init_time desc

select

from tms_tray_info t
left join tms_rfid_bind_spurtcode s on s.rfid = t.rfid and s.factory_code =t.factory_code and s.is_having =0 and s.delete_flag =0
left join tms_rfid_bind_order_detail d on d.rfid = t.rfid and d.current_code = s.current_code and d.delete_flag =0
left join tms_rfid_bind_order o on o.id =d.pid and o.delete_flag=0
where t.delete_flag=0

    and t.rfid = #{rfid,jdbcType=VARCHAR}


    and t.rfid_type = #{rfidType,jdbcType=INTEGER}


    and t.rfid_status !=2


    and t.rfid_health = #{rfidHealth,jdbcType=INTEGER}


    and date_format(t.init_time,'%Y-%m-%d') >= #{startTime,jdbcType=VARCHAR}


    and date_format(t.init_time,'%Y-%m-%d') <= #{endTime,jdbcType=VARCHAR}


    and o.order_no = #{orderNo,jdbcType=VARCHAR}


    and s.current_code = #{currentCode,jdbcType=VARCHAR}

order by t.init_time desc

select factory_code, factory_name, rfid_type, case when rfid_type =1 then '木质' when rfid_type =2 then '塑胶' else '其它' end rfid_type_Name, rfid_status, case when rfid_status =1 then '满托' when rfid_status =2 then '空托' when rfid_status =3 then '在途(中转库)' when rfid_status =4 then '异地(客户)' when rfid_status =5 then '已装车' else '其它' end rfid_status_name, count(rfid) rfid_count from tms_tray_info where delete_flag =0
and rfid = #{rfid,jdbcType=VARCHAR}
and rfid_type = #{rfidType,jdbcType=INTEGER}
and rfid_status = #{rfidStatus,jdbcType=INTEGER}
group by factory_code, factory_name, rfid_type, rfid_status order by factory_code,rfid_status

mapper.update:
update tms_tray_info
rfid_status = #{rfidStatus}, update_by = #{updateBy}, init_time = #{initTime}, binding_time = #{bindingTime}, leave_time = #{leaveTime}, return_time = #{returnTime}, update_time = #{updateTime}, remarks= CONCAT(IFNULL(remarks,''), #{remarks}), version= #{version}, rfid_health= #{rfidHealth}
where delete_flag =0
and factory_code=#{factoryCode,jdbcType=VARCHAR}
and rfid =#{rfid,jdbcType=VARCHAR}

update tms_tray_info
rfid_status = #{rfidStatus}, update_by = #{updateBy}, leave_time =current_timestamp, update_time =current_timestamp
where rfid in ( select d.rfid from tms_rfid_bind_order t inner join tms_rfid_bind_order_detail d on t.id=d.pid where t.order_no=#{orderNo,jdbcType=VARCHAR}
and t.factory_code=#{factoryCode,jdbcType=VARCHAR}
and d.delete_flag =0 ) and delete_flag =0

mapper.select:
select * from tms_tray_info
 where delete_flag =0

    and factory_code=#{factoryCode}

 and rfid =#{rfid}
